mapper.select:
select 

from record
where sender = #{sender,jdbcType=INTEGER}
  and receiver = #{receiver,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}

mapper.delete:
delete from record
where sender = #{sender,jdbcType=INTEGER}
  and receiver = #{receiver,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}

mapper.update:
update record

  
    content = #{content,jdbcType=VARCHAR},
  

where sender = #{sender,jdbcType=INTEGER}
  and receiver = #{receiver,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}

update record
set content = #{content,jdbcType=VARCHAR}
where sender = #{sender,jdbcType=INTEGER}
  and receiver = #{receiver,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}
